import React, {useEffect, useState} from 'react';

const useAuth = (jwt) => {
    const [isAuth, setIsAuth] = useState(true);

    useEffect(() => {
        if(jwt.length > 25) {
            setIsAuth(true);
        }else{
            setIsAuth(false);
        }
    }, []);

    
    return isAuth;
}

export default useAuth
