                            helperText={
                            email.valid === false && "Ingresa un correo electrónico válido."
                            }
                            value={email.value}
                            onChange={(input) => {
                            const email = input.target.value;
                            const valido = validarEmail(email);
                            setEmail({ value: email, valid: valido });
                            }}
                        />
                    )
                })
            }
        <Button variant="contained" type="submit">
            {buttonText}
        </Button>
        </Box>
    );
};

export default Step;
