  }



  return (
    <Box
      sx={{
        padding: "30px",
        display: "flexbox",
        flexDirection: "column",
      }}
    >
      <LogoSpace>
        <Img src={"/favicon.png"} />
        <Typography variant="h3">AluraFood</Typography>
      </LogoSpace>
      <FormSpace>
        { (step < 3) && <Stepper step={step} />}
        {steps[step]}
      </FormSpace>
    </Box>
  );
};

export default Form;
