import React, {useState} from "react";
import { TextField, Button, Box } from "@mui/material";
import { validarNombre, validarApellido, validarTelefono } from "./validaciones";

const DatosPersonales = ({ updateStep }) => {
  const [name, setName] = useState({ value: "", valid: null })
  const [lastName, setlastName] = useState({ value: "", valid: null })
  const [phone, setPhone] = useState({ value: "", valid: null })

  return (
    <Box
      component="form"
      autoComplete="off"
      sx={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        flexDirection: "column",
      }}
      onSubmit={(e)=> {
        e.preventDefault();
        updateStep(2);
      }}
    >
      <TextField
        label="Nombre"
        variant="outlined"
        fullWidth
        margin="dense"
        type="text"
        value={name.value}
        onChange={(input) =>{
          const value = input.target.value
          const valid = validarNombre(value)
          setName({value, valid})
          console.log(value, valid)
        }}
        error={name.valid == false}
        helperText={
          lastName.valid == false && "Ingresa al menos 2 carácteres y máximo 30."
        }
      />

      <TextField
        label="Apellidos"
        variant="outlined"
        fullWidth
        margin="dense"
        type="text"
        value={lastName.value}
        onChange={(input) =>{
          const value = input.target.value
          const valid = validarApellido(value)
          setlastName({value, valid})
          console.log(value, valid)
        }}
